// Ex 12: 13: 14: Ex 15:
var s = 0;
function play(question, answer) {
    var userAns = readlineSync.question(question);
    if (answer === userAns) {
        s++;
        console.log("yeah!! you're right");
    } else {
        s--;
        console.log("ohh!! you're wrong");
    }
    console.log("Your current score:", s);
    console.log("-----------------------");
}
var quesOne = {
    question: "Who is your fav super hero? ",
    answer: "batman",
};

var quesTwo = {
    question: "Who is your fav song? ",
    answer: "saiyaan",
};
var quesThree = {
    question: "Are you Adult? ",
    answer: "yes",
};

var quesFour = {
    question: "Are you from Bihar? ",
    answer: "yes",
};

var quesFive = {
    question: "Are you a female? ",
    answer: "no",
};

var question = [
    { question: "Who is your fav super hero? ", answer: "batman" },
    { question: "Who is your fav song? ", answer: "saiyaan" },
    { question: "Are you Adult? ", answer: "yes" },
    { question: "Are you from Bihar? ", answer: "yes" },
    { question: "Are you a female? ", answer: "no" },
];

for (let i = 0; i < question.length; i++) {
    var element = question[i];
    play(element.question, element.answer);
}
